package neetCode150.slidingWindow;

import java.util.HashMap;
import java.util.Map;

// https://leetcode.com/problems/permutation-in-string/description/
public class PermutationInString {
    /* Sliding Window Solution
    Time complexity:O(n)
    Space complexity:O(1)
    */
    public static boolean checkInclusion(String s1, String s2) {
        if (s1.length() > s2.length())
            return false;

        int[] s1Count = new int[26]; // Frequency map for s1
        int[] s2Count = new int[26]; // Frequency map for s2
        // Populate the frequency map for s1
        for (int i = 0; i < s1.length(); i++) {
            s1Count[s1.charAt(i) - 'a']++;
        }
        int windowSize = s1.length();
        // Initialize the first window in s2
        for (int i = 0; i < windowSize; i++) {
            s2Count[s2.charAt(i) - 'a']++;
        }
        // Check if the first window matches
        if (matches(s1Count, s2Count))
            return true;
        // Slide the window through s2
        for (int i = windowSize; i < s2.length(); i++) {
            // Add the next character to the window
            s2Count[s2.charAt(i) - 'a']++;
            // Remove the first character of the previous window
            s2Count[s2.charAt(i - windowSize) - 'a']--;

            // Check if the updated window matches
            if (matches(s1Count, s2Count))
                return true;
        }
        return false;
    }

    // Helper method to compare two frequency maps
    private static boolean matches(int[] s1Count, int[] s2Count) {
        for (int i = 0; i < 26; i++) {
            if (s1Count[i] != s2Count[i]) return false;
        }
        return true;
    }

    // This failed leetcode Test case
    /*
    Time complexity:O(n*m)
    Space complexity:O(1)
    Where n is the length of the string s1 and m is the of string s2.
    This solution uses a nested loop with a HashMap to determine if any substring of s2 is a permutation of s1.
    This approach compares character frequencies for every possible starting point in s2.
    */
    public static boolean checkInclusionUsingHashMap(String s1, String s2) {
        // Step 1: Create a frequency map for characters in s1
        Map<Character, Integer> s1FrequencyMap = new HashMap<>();
        for (char c : s1.toCharArray()) {
            s1FrequencyMap.put(c, s1FrequencyMap.getOrDefault(c, 0) + 1);
        }

        // `requiredMatches` represents the number of unique characters to match
        int requiredMatches = s1FrequencyMap.size();

        // Step 2: Iterate through all possible starting points in s2
        for (int start = 0; start < s2.length(); start++) {
            // Frequency map for the current window in s2
            Map<Character, Integer> windowFrequencyMap = new HashMap<>();
            int matchedCharacters = 0;

            // Expand the window starting at `start`
            for (int end = start; end < s2.length(); end++) {
                char currentChar = s2.charAt(end);

                // Update the frequency of the current character in the window
                windowFrequencyMap.put(currentChar, windowFrequencyMap.getOrDefault(currentChar, 0) + 1);

                // If the frequency of the current character exceeds the required frequency, break
                if (s1FrequencyMap.getOrDefault(currentChar, 0) < windowFrequencyMap.get(currentChar)) {
                    break;
                }

                // If the character's frequency matches between s1 and the current window, count it as matched
                if (s1FrequencyMap.getOrDefault(currentChar, 0) == windowFrequencyMap.get(currentChar)) {
                    matchedCharacters++;
                }

                // If all characters match, we found a valid permutation
                if (matchedCharacters == requiredMatches) {
                    return true;
                }
            }
        }

        // If no valid permutation is found
        return false;
    }


    public static void main(String[] args) {
        System.out.println(checkInclusion("ab", "eidbaooo"));
        System.out.println(checkInclusion("ab", "eidboaoo"));
        System.out.println(checkInclusionUsingHashMap("ab", "eidbaooo"));
        System.out.println(checkInclusionUsingHashMap("ab", "eidboaoo"));

        System.out.println("Testing leetcode edge case for both approaches");
        String s1 = "";
        String s2 = "";
        System.out.println(checkInclusion(s1,s2)); // pass
        System.out.println(checkInclusionUsingHashMap(s1,s2));// fails
    }
}
